public class week51 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

			  for(int i=1; i<=5;i++){

				  

				   for (int l =5 ;l >i ; l--){

				    System.out.print(" ");

				   }

				   for (int k =1; k<=i;k++){

				    System.out.print("*");

				    System.out.print(" ");

				   }

				   System.out.println();


				  }

			   }
	}

	
	
