package week5;

public class week52 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i =5; i>=1;i--){

			   for (int l =i ;l <5 ; l++){

			    System.out.print(" ");

			   }

			   for (int k =1; k<=i;k++){

			    System.out.print("*");

			    System.out.print(" ");

			   }

			   System.out.println();

			  }

			 }


	}


